import React, { useEffect, useRef, useState } from 'react';
import { useSocket } from './socket/socket';

function Chat({ username }) {
  const {
    connect,
    disconnect,
    messages,
    sendMessage,
    typingUsers,
    setTyping,
    users
  } = useSocket();

  const [message, setMessage] = useState('');
  const messageRef = useRef();

  useEffect(() => {
    connect(username);
    return () => disconnect();
  }, [username]);

  useEffect(() => {
    messageRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = () => {
    if (message.trim()) {
      sendMessage(message);
      setMessage('');
    }
  };

  return (
    <div className="max-w-2xl w-full mx-auto p-4">
      <div className="bg-white rounded shadow p-4">
        <h3 className="text-xl font-semibold mb-2">Welcome, {username} ðŸ‘‹</h3>
        <p className="text-sm text-gray-600 mb-2">
          Online: {users.map(u => u.username).join(', ') || 'No users online'}
        </p>

        <div className="border rounded h-64 overflow-y-scroll p-2 bg-gray-50">
          {messages.map((msg) => (
            <div key={msg.id} className="mb-2">
              {msg.system ? (
                <i className="text-gray-500 text-sm">{msg.message}</i>
              ) : (
                <span>
                  <b className="text-blue-700">{msg.sender}:</b> {msg.message}
                </span>
              )}
            </div>
          ))}
          <div ref={messageRef}></div>
        </div>

        {typingUsers.length > 0 && (
          <p className="italic text-sm text-gray-500 mt-1">
            {typingUsers.join(', ')} is typing...
          </p>
        )}

        <div className="flex mt-4 gap-2">
          <input
            className="flex-1 border rounded px-3 py-2 focus:outline-none focus:ring"
            value={message}
            placeholder="Type a message..."
            onChange={(e) => {
              setMessage(e.target.value);
              setTyping(e.target.value !== '');
            }}
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          />
          <button
            onClick={handleSend}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
