import React, { useState } from 'react';
import Chat from './Chat';

function App() {
  const [username, setUsername] = useState('');
  const [joined, setJoined] = useState(false);

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      {joined ? (
        <Chat username={username} />
      ) : (
        <div className="bg-white shadow-lg rounded p-8 w-96">
          <h2 className="text-2xl font-bold mb-4 text-center">Join Chat</h2>
          <input
            className="border rounded w-full px-3 py-2 mb-4 focus:outline-none focus:ring"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter your name"
          />
          <button
            onClick={() => username && setJoined(true)}
            className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
          >
            Join
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
