import React, { useEffect, useRef, useState } from 'react';
import { useSocket } from './socket/socket';

function Chat({ username }) {
  const {
    connect,
    disconnect,
    messages,
    sendMessage,
    typingUsers,
    setTyping,
    users
  } = useSocket();

  const [message, setMessage] = useState('');
  const messageRef = useRef();

  useEffect(() => {
    connect(username);
    return () => disconnect();
  }, [username]);

  useEffect(() => {
    messageRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = () => {
    if (message.trim()) {
      sendMessage(message);
      setMessage('');
    }
  };

  return (
    <div>
      <h3>Welcome, {username}!</h3>
      <div><strong>Online Users:</strong> {users.map(u => u.username).join(', ')}</div>
      <div style={{ border: '1px solid gray', height: 300, overflowY: 'scroll', margin: '10px 0', padding: 5 }}>
        {messages.map((msg) => (
          <div key={msg.id}>
            {msg.system ? (
              <i>{msg.message}</i>
            ) : (
              <>
                <strong>{msg.sender}:</strong> {msg.message}
              </>
            )}
          </div>
        ))}
        <div ref={messageRef}></div>
      </div>
      {typingUsers.length > 0 && (
        <div><i>{typingUsers.join(', ')} typing...</i></div>
      )}
      <input
        value={message}
        placeholder="Type message..."
        onChange={(e) => {
          setMessage(e.target.value);
          setTyping(e.target.value !== '');
        }}
        onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        style={{ width: '80%' }}
      />
      <button onClick={handleSend}>Send</button>
    </div>
  );
}

export default Chat;
